// LandingPage.js

import React, { useState } from "react";
import "./LandingPage.css";
import CircleProgressBar from "react-circle-progress-bar";

function LandingPage() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });
  const [loading, setLoading] = useState(false);
  const [alertMessage, setAlertMessage] = useState(null);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await fetch("http://localhost:5001/api/send-email", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setAlertMessage("Message sent successfully!");
      } else {
        setAlertMessage("Error sending message");
      }
    } catch (error) {
      console.error("Error:", error);
      setAlertMessage("Error sending message");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <div className="landing-page">
        <h1>Welcome to Our Project</h1>
        <p>
          This is a brief description of our project. You can provide details
          about the purpose, features, and goals of the project here.
        </p>

        <div className="divider"></div>

        <div className="developers-section">
          <h2>Meet Our Developers</h2>
          {/* ... (rest of your components) */}
        </div>

        <div className="divider"></div>

        <div className="about-us-section">
          <h2>About Us</h2>
          {/* ... (rest of your components) */}
        </div>

        <div className="divider"></div>

        <div className="contact-us-section">
          <h2>Contact Us</h2>
          {/* ... (rest of your components) */}

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="name">Name:</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="email">Email:</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="message">Message:</label>
              <textarea
                id="message"
                name="message"
                value={formData.message}
                onChange={handleChange}
                required
              ></textarea>
            </div>

            <button type="submit" disabled={loading}>
              {loading ? (
                <CircleProgressBar
                  strokeWidth={5}
                  percentage={100}
                  initialAnimation
                  animate
                  textColor="#000"
                  fontSize="20px"
                  textStyle={{
                    font: "bold 5rem 'Roboto, sans-serif'",
                  }}
                  onComplete={() => null}
                />
              ) : (
                "Send Message"
              )}
            </button>
          </form>

          {alertMessage && (
            <div
              className={
                alertMessage.includes("success")
                  ? "success-alert"
                  : "error-alert"
              }
            >
              {alertMessage}
            </div>
          )}
        </div>
      </div>
    </>
  );
}

export default LandingPage;
