// Profile.js

import React, { useState, useEffect } from "react";
import defaultUserImage from "../assets/defaultUserImage.png"; // Import your default user image
import "./Profile.css";

const Profile = () => {
  const [userData, setUserData] = useState({
    username: "Default Username",
    name: "Default Name",
    email: "default@example.com",
    // Add other user data fields as needed
  });
  const [editedUserData, setEditedUserData] = useState({
    username: "",
    name: "",
    email: "",
    // Add other user data fields as needed
  });

  const [isProfileZoomed, setIsProfileZoomed] = useState(false);
  const [isPasswordUpdateZoomed, setIsPasswordUpdateZoomed] = useState(false);

  // Function to simulate updating user details in the database
  const updateProfile = (newData) => {
    // Simulate updating user details in the database
    console.log("Updating profile details:", newData);

    // Update state with the new user data
    setUserData(newData);

    // Reset the zoomed-in state
    setIsProfileZoomed(false);
  };

  // Function to simulate updating password in the database
  const updatePassword = (currentPassword, newPassword) => {
    // Simulate updating password in the database
    console.log("Updating password:", currentPassword, newPassword);

    // Reset the zoomed-in state
    setIsPasswordUpdateZoomed(false);
  };

  // Simulate fetching user data from the database
  useEffect(() => {
  const fetchUserData = async () => {
    try {
      const token = localStorage.getItem("token"); // Retrieve the token from local storage
      const response = await fetch("/api/user", {
        headers: {
          Authorization: token,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setUserData(data || userData);
      } else {
        console.error("Error fetching user data:", response.statusText);
      }
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  fetchUserData();
}, []);

  return (
    <div className="profile-body">
      <div className="profile-card">
        <img
          src={userData.image || defaultUserImage}
          alt="User Profile"
          className="profile-image"
        />
        <div className="user-details">
          <h2>{userData.username}</h2>
          <p>Name: {userData.name}</p>
          <p>Email: {userData.email}</p>
          {/* Add other user details here */}
          <button onClick={() => setIsProfileZoomed(true)}>
            Update Profile
          </button>
          <button onClick={() => setIsPasswordUpdateZoomed(true)}>
            Update Password
          </button>
        </div>
      </div>

      {isProfileZoomed && (
    <div className="zoomed-card">
      <div className="profile-card">
        <img
          src={userData.image || defaultUserImage}
          alt="User Profile"
          className="profile-image"
        />
        <div className="user-details">
          <h2>{editedUserData.username || userData.username}</h2>
          <p>Name: {editedUserData.name || userData.name}</p>
          <p>Email: {editedUserData.email || userData.email}</p>
          {/* Add other user details here */}

          {/* Input fields for editing user details */}
          <input
            type="text"
            placeholder="Enter new username"
            value={editedUserData.username}
            onChange={(e) => setEditedUserData({ ...editedUserData, username: e.target.value })}
          />
          <input
            type="text"
            placeholder="Enter new name"
            value={editedUserData.name}
            onChange={(e) => setEditedUserData({ ...editedUserData, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Enter new email"
            value={editedUserData.email}
            onChange={(e) => setEditedUserData({ ...editedUserData, email: e.target.value })}
          />

          <button onClick={() => updateProfile(editedUserData)}>
            Update Profile
          </button>
          <button onClick={() => setIsProfileZoomed(false)}>Close</button>
        </div>
      </div>
    </div>
  )}


      {/* Zoomed-in Password Update Card */}
      {isPasswordUpdateZoomed && (
        <div className="zoomed-card">
          <div className="profile-card">
            <form>
              <input type="password" placeholder="Current Password" />
              <input type="password" placeholder="New Password" />
              <input type="password" placeholder="Confirm New Password" />
              <button
                onClick={() => updatePassword("currentPassword", "newPassword")}
              >
                Update
              </button>
              <button
                className="close"
                onClick={() => setIsPasswordUpdateZoomed(false)}
              >
                Close
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Profile;
